package com.assignment.ForLoop;

public class ForLoopAssignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum=0;
		
		for(int a=1;a<=10; ++a){
			sum=sum+a;}
		
		
		
		System.out.println("the sum of first 10 natural no. is  " + sum);
	
		
	/*	here we assing a=1 ; 
		the loop process start and perform------- sum=sum+a --------------sum=0+1
		now itwill perfom-------- ++a-------a=a+1-------i.e a=2
		now the loop will continue till it become false*/
		
		
		
	}

}
